mapper.insert:
insert into 
		reply( idx, writer, content)
value
	(
	  #{idx}, #{writer}, #{content}
	)

mapper.update:
update reply set
writer = #{writer},
content = #{content}
where replyIdx = #{replyIdx}
and idx = #{idx}

mapper.delete:
delete from reply
where replyIdx = #{replyIdx}
and idx = ${idx}
